export class AuthService {
    constructor(http) {
        this.http = http;
        //private authInterceptor:AuthInterceptor;
        this.urlRegister = "http://localhost:3000/signup";
        this.urlLogin = "http://localhost:3000/signin";
        //this.authInterceptor = new AuthInterceptor();
    }
    signUp(registerData) {
        return this.http.post(this.urlRegister, registerData);
    }
    async login(username, password) {
        console.log("logging...");
        let logged = false;
        await this.http.post(this.urlLogin, { username, password }).subscribe({
            next: (data) => {
                if (data.logged) {
                    console.log(data);
                    this.setSession(data.jwt, username);
                    logged = true;
                }
            },
            error: (error) => {
                console.error('There was an error!', error);
            }
        });
        return logged;
    }
    setSession(authResult, username) {
        //const expiresAt = moment().add(authResult.expiresIn,'second');
        localStorage.setItem('jwt', authResult.token);
        localStorage.setItem('username', username);
    }
    logout() {
        localStorage.removeItem("jwt");
        localStorage.removeItem("username");
    }
}
